<div class="actionToolbar"><span class="actionselecTitem">Select Item(s)</span>
  <div class="actionButton">
    <button type="button" class="btn btn-action" title="Copy" data-role="img-copy" data-toggle="tooltip" data-rolesetting="allow_item_copy"><i class="iconaction iconCopy"></i></button>
    <button type="button" class="btn btn-action" title="Transfer" data-role="img-Transfer" data-rolesetting="allow_item_transfer" data-toggle="tooltip"><i class="iconaction iconTransfer"></i></button>
    <button type="button" class="btn btn-action" title="Edit image" data-role="img-edit" data-rolesetting="allow_item_edit" data-toggle="tooltip"><i class="iconaction iconEditImage"></i></button>
    <button type="button" class="btn btn-action" title="Rename" data-role="img-rename" data-rolesetting="allow_item_rename" data-toggle="tooltip"><i class="iconaction iconRename"></i></button>
    <button type="button" class="btn btn-action" title="Download" data-role="img-download" data-rolesetting="allow_item_download" data-toggle="tooltip"><i class="iconaction iconDownload"></i></button>
   
    <button type="button" class="btn btn-action" title="View" data-role="img-view" data-rolesetting="allow_item_view" data-toggle="tooltip"><i class="iconaction iconView"></i></button>
    <button type="button" class="btn btn-action btn-delete"  data-role="img-delete" data-rolesetting="allow_item_delete" data-toggle="tooltip" title="Delete"><i class="iconaction iconDelete"></i></button>
    <button type="button" class="btn btn-action" data-toggle="tooltip" data-rolesetting="allow_item_order" title="In order"><i class="iconaction iconInorder"></i></button>
  </div>
</div>
